    parentName: "pre"
  }, {}), "~/bin/i3audio.sh\n\n\n#!/usr/bin/env bash\nset -Ceuo pipefail\n\nTARGET=\"$1\"\n\nAUDIOPATH=\"/var/tmp/i3audio.txt\"\n\n# check param\nif [ x$TARGET = \"x\" ]; then\n  exit 1\nfi\n\n# check audio index\nif [ -f \"$AUDIOPATH\" ]; then\n  # exists\n  INDEX=$(cat \"$AUDIOPATH\")\nelse\n  # not exists\n  echo -n \"0\" > \"$AUDIOPATH\"\n  INDEX=\"0\"\nfi\n\n# set index\nif [ $TARGET = \"index\" ]; then\n  # echo pacmd list-sinks\n  CNT=0\n  pacmd list-sinks | while read LINE\n  do\n    CNT=$(expr $CNT + 1)\n    if [ \"`echo $LINE | grep -e 'index:'`\" ]; then\n      echo \"$LINE\"\n    elif [ \"`echo $LINE | grep -e 'state:' -e 'volume:' -e 'device.description ='`\" ]; then\n      echo \"   $LINE\"\n    fi\n  done\n  # read index number\n  while :\n  do\n    read -p \"Input index number (number or c=cancel): \" DATA\n    # check input number\n    if [ \"$DATA\" = \"c\" ]; then\n      echo \"Canceled.\"\n      break\n    else\n      expr \"$DATA\" + 1 > /dev/null 2>&1\n      if [ $? -lt 2 ]; then\n        # is Numeric\n        echo -n \"$DATA\" >| \"$AUDIOPATH\"\n        pacmd set-default-sink \"$DATA\"\n        echo \"Done.\"\n        break\n      else\n        # is not Numeric\n        break\n      fi\n    fi\n  done\nfi\n\n# volume up\nif [ $TARGET = \"up\" ]; then\n  pactl set-sink-volume $INDEX +5%\nfi\n\n# volume down\nif [ $TARGET = \"down\" ]; then\n  pactl set-sink-volume $INDEX -5%\nfi\n\n# volume toggle\nif [ $TARGET = \"toggle\" ]; then\n  pactl set-sink-mute $INDEX toggle\nfi\n\n")), mdx("h3", {
